package com.kelly.protocol

/**
 * Parameter definition for a calibration value.
 */
data class ParameterDef(
    val offset: Int,
    val size: ParamSize,
    val position: Int,
    val type: ParamType,
    val name: String,
    val minValue: Int,
    val maxValue: Int,
    val visible: Boolean,
    val editable: Boolean,
    val tips: String,
    val category: ParamCategory = ParamCategory.GENERAL,
    val safety: SafetyLevel = SafetyLevel.SAFE
)

/**
 * Calibration parameter definitions for supported controller models.
 * Extracted from ACAduserEnglishSetting.java - only KBLS0106 and KBLS0109.
 */
object ParameterDefinitions {
    /**
     * Get parameter definitions for a specific controller model.
     */
    fun getParameters(model: ControllerModel): List<ParameterDef> = when (model) {
        ControllerModel.KBLS_0106 -> KBLS_0106_PARAMS
        ControllerModel.KBLS_0109 -> KBLS_0109_PARAMS
    }

